``` java
import java.nio.charset.StandardCharsets;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import javax.crypto.Mac;
import javax.crypto.spec.SecretKeySpec;
import java.util.Base64;
public class AiraloWebhookVerification {
    private static final String API_SECRET = "<YOUR_API_SECRET>";
    public static void main(String[] args) {
        // This is the JSON payload pushed by Airalo's webhook. Depending on the framework you are using, retrieve the payload.
        // Assign the actual payload here
        String payload = "{}";  // Replace with the actual payload
        // Assign the actual header here, located in key `airalo-signature`
        String airaloSignature = "<AIRALO_SIGNATURE>";
        if (payload instanceof String) {
            try {
                MessageDigest digest = MessageDigest.getInstance("SHA-512");
                byte[] hash = digest.digest(payload.getBytes(StandardCharsets.UTF_8));
                payload = bytesToHex(hash);
            } catch (NoSuchAlgorithmException e) {
                e.printStackTrace();
            }
        }
        String expectedSignature = generateHmacSHA512(API_SECRET, payload);
        if (expectedSignature.equals(airaloSignature)) {
            // Here you are guaranteed the payload came from Airalo's system, and it is not from any third party or attacker.
            // You can safely proceed with your flow.
            System.out.println("Yay!");
        } else {
            // We wouldn't trust this payload and the system that sent it... Better to reject it or proceed at your own risk.
            System.out.println("Hmm.... it is suspicious");
        }
    }
    private static String generateHmacSHA512(String key, String data) {
        try {
            Mac sha512_HMAC = Mac.getInstance("HmacSHA512");
            SecretKeySpec secret_key = new SecretKeySpec(key.getBytes(StandardCharsets.UTF_8), "HmacSHA512");
            sha512_HMAC.init(secret_key);
            byte[] bytes = sha512_HMAC.doFinal(data.getBytes(StandardCharsets.UTF_8));
            return bytesToHex(bytes);
        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }
    }
    private static String bytesToHex(byte[] bytes) {
        StringBuilder result = new StringBuilder();
        for (byte b : bytes) {
            result.append(String.format("x", b));
        }
        return result.toString();
    }
}

