#!/usr/bin/env node

const GRAPHQL_ENDPOINT = 'http://localhost:5001/graphql';
const AUTH_TOKEN = 'Bearer eyJhbGciOiJIUzI1NiIsImtpZCI6IngrNVFCMkZZbGhZTitUZmQiLCJ0eXAiOiJKV1QifQ..cRq3lorN5L84-6mtcq8DBV6CVmU1t396fdzpUbf0_qw';

const mutation = `
  mutation TriggerCatalogSync($params: CatalogSyncParams!) {
    triggerCatalogSync(params: $params) {
      success
      jobId
      message
      error
    }
  }
`;

const variables = {
  params: {
    type: 'FULL_SYNC',
    priority: 'high',
    force: true
  }
};

async function triggerSync() {
  console.log('üöÄ Triggering catalog sync...\n');
  
  try {
    const response = await fetch(GRAPHQL_ENDPOINT, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': AUTH_TOKEN
      },
      body: JSON.stringify({
        query: mutation,
        variables: variables
      })
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const result = await response.json();
    
    if (result.errors) {
      console.error('‚ùå GraphQL Errors:', result.errors);
      return;
    }

    if (result.data?.triggerCatalogSync) {
      const syncResult = result.data.triggerCatalogSync;
      if (syncResult.success) {
        console.log('‚úÖ Catalog sync triggered successfully!');
        console.log(`   Job ID: ${syncResult.jobId}`);
        console.log(`   Message: ${syncResult.message}`);
        console.log('\nüìù The worker should now pick up this job and start processing.');
        console.log('   Check the worker logs for progress updates.');
      } else {
        console.error('‚ùå Failed to trigger sync:', syncResult.error);
      }
    }
  } catch (error) {
    console.error('‚ùå Error triggering sync:', error);
  }
}

triggerSync();